object.create:
CREATE FUNCTION [dbo].[PenaltiesCalculation] ( @contract_id INT, @installment_number INT, @endDate datetime) RETURNS MONEY AS BEGIN DECLARE @Penalties TABLE ( IDD INT IDENTITY(1, 1), expected_date datetime, capital_repayment money, interest_repayment money, contract_id int NOT NULL, paid_capital money, paid_date datetime, number int, Penalties_based_on_overdue_principal MONEY, Penalties_based_on_overdue_interest MONEY, Penalties_based_on_OLB MONEY, Penalties_based_on_initial_amount MONEY )
DECLARE @counter INT DECLARE @LastInst INT DECLARE @I INT DECLARE @Date1 DATETIME DECLARE @LATE_DAYS_AFTER_ACCRUAL_CEASES INT DECLARE @Penalties_based_on_overdue_principal FLOAT DECLARE @Penalties_based_on_overdue_interest FLOAT DECLARE @Penalties_based_on_OLB FLOAT DECLARE @Penalties_based_on_initial_amount FLOAT DECLARE @TotalPenalties FLOAT DECLARE @expected_principal FLOAT DECLARE @expected_interests FLOAT DECLARE @value_first_inst_principal FLOAT DECLARE @value_first_inst_interest FLOAT DECLARE @OLB FLOAT DECLARE @Last_repayment_date DATETIME DECLARE @days_late INT SET @Last_repayment_date = (select max(event_date) from contractEvents inner join repaymentEvents on repaymentEvents.id = contractEvents.id where contractEvents.contract_id = @contract_id and is_deleted=0) SET @days_late = CASE WHEN dbo.getLateDays(@endDate, @contract_Id,@installment_number) < DATEDIFF(d,@Last_repayment_date,@endDate) THEN DATEDIFF(d,@Last_repayment_date,@endDate) ELSE dbo.getLateDays(@endDate, @contract_Id,@installment_number) END
SELECT @LATE_DAYS_AFTER_ACCRUAL_CEASES = CONVERT(INT, value) FROM GeneralParameters WHERE [key] = 'LATE_DAYS_AFTER_ACCRUAL_CEASES'
SELECT @Penalties_based_on_overdue_principal = non_repayment_penalties_based_on_overdue_principal, @Penalties_based_on_overdue_interest = non_repayment_penalties_based_on_overdue_interest, @Penalties_based_on_OLB = non_repayment_penalties_based_on_OLB, @Penalties_based_on_initial_amount = non_repayment_penalties_based_on_initial_amount FROM dbo.Contracts INNER JOIN Credit ON contracts.id = credit.id inner join projects on projects.id = contracts.project_id WHERE Contracts.id = @contract_id INSERT INTO @Penalties ( expected_date, capital_repayment, interest_repayment, contract_id, paid_capital, paid_date, number ) SELECT expected_date, capital_repayment, interest_repayment, contract_id, paid_capital, paid_date, number FROM dbo.Installments WHERE contract_id = @contract_id AND number >= @installment_number AND expected_date <= @endDate SET @counter = @installment_number; SET @LastInst = (select max(number) from @Penalties);
WHILE ( @counter <= @LastInst) BEGIN
SELECT @Date1 = ISNULL(paid_date, expected_date), @expected_principal = capital_repayment, @expected_interests = interest_repayment FROM @Penalties WHERE number = @counter
SET @value_first_inst_principal = ( (isnull((select sum(capital_repayment) from installments where installments.contract_id = @contract_id and number <= @counter),0)) - (isnull((select sum(principal) from contractEvents inner join repaymentEvents on repaymentEvents.id = contractEvents.id where contractEvents.contract_id = @contract_id and is_deleted=0 and event_date <= @endDate),0)) ) SET @value_first_inst_interest = ( (isnull((select sum(interest_repayment) from installments where installments.contract_id = @contract_id and number <= @counter),0)) - (isnull((select sum(interests) from contractEvents inner join repaymentEvents on repaymentEvents.id = contractEvents.id where contractEvents.contract_id = @contract_id and is_deleted=0 and event_date <= @endDate),0)) ) UPDATE @Penalties SET Penalties_based_on_overdue_interest = @Penalties_based_on_overdue_interest * (CASE WHEN (@counter = @installment_number) and (@value_first_inst_interest < @expected_interests) then 0 else (@expected_interests) END) * CASE WHEN DATEDIFF(day, @Date1, @endDate) > @LATE_DAYS_AFTER_ACCRUAL_CEASES THEN @LATE_DAYS_AFTER_ACCRUAL_CEASES ELSE DATEDIFF(day, @Date1, @endDate) END WHERE number = @counter ; UPDATE @Penalties SET Penalties_based_on_overdue_principal = @Penalties_based_on_overdue_principal * (CASE WHEN (@counter = @installment_number) and (@value_first_inst_principal < @expected_principal) then 0 else (@expected_principal) END) * CASE WHEN DATEDIFF(day, @Date1, @endDate) > @LATE_DAYS_AFTER_ACCRUAL_CEASES THEN @LATE_DAYS_AFTER_ACCRUAL_CEASES ELSE DATEDIFF(day, @Date1, @endDate) END WHERE number = @counter ;
SET @counter = @counter + 1; END SET @OLB = ISNULL ( (SELECT credit.amount FROM credit WHERE (credit.id = @contract_id)) - (select sum(principal) from contractEvents inner join repaymentEvents on repaymentEvents.id = contractEvents.id where contractEvents.contract_id = @contract_id and is_deleted=0 and event_date <= @endDate) , 0) UPDATE @Penalties SET Penalties_based_on_OLB = @Penalties_based_on_OLB * @OLB * CASE WHEN datediff(day,@Last_repayment_date,@endDate) < @days_late then datediff(day,@Last_repayment_date,@endDate) else @days_late end WHERE number = @counter-1 ;
UPDATE @Penalties SET Penalties_based_on_initial_amount = @Penalties_based_on_initial_amount * (select credit.amount from credit where credit.id = @contract_id) * CASE WHEN datediff(day,@Last_repayment_date,@endDate) < @days_late then datediff(day,@Last_repayment_date,@endDate) else @days_late end WHERE number = @counter-1 ; SELECT @TotalPenalties = (isnull(SUM(Penalties_based_on_initial_amount),0) +isnull(sum(Penalties_based_on_OLB),0) +isnull(sum(Penalties_based_on_overdue_principal),0) +isnull(sum(Penalties_based_on_overdue_interest),0)) FROM @Penalties RETURN round(@TotalPenalties,2) END

CREATE function [dbo].[next_contractEvents]
(@contractEvents int)
RETURNS int
AS
BEGIN
DECLARE @contractId int;
DECLARE @event_date DATETIME;
DECLARE @next_contractEvents INT;
SET @contractId = (SELECT contract_id FROM [ContractEvents] WHERE id = @contractEvents);
SET @event_date = (SELECT event_date FROM [ContractEvents] WHERE id = @contractEvents);
SET @next_contractEvents = (SELECT TOP 1 id 
							FROM [ContractEvents] 
							WHERE contract_id = @contractId AND event_date > @event_date);
return @next_contractEvents;
END

CREATE FUNCTION SavingDeposits_MC
(
@pFrom DATETIME,
@pTo DATETIME,
@pWithdrawedIn INT,
@pDisplayIn INT
) RETURNS TABLE
AS RETURN
(
 SELECT d.contract_id,
  d.event_id,
  d.event_date,
  d.amount * dbo.GetXR(d.currency_id,@pDisplayIn,d.event_date) AS amount,
  d.method,
  d.product_type
FROM SavingDeposits(@pFrom,@pTo) d
WHERE d.currency_id = @pWithdrawedIn OR 0 = @pWithdrawedIn
)

CREATE FUNCTION SavingWithdrawals_MC
(
@pFrom DATETIME,
@pTo DATETIME,
@pWithdrawedIn INT,
@pDisplayIn INT
) RETURNS TABLE
AS RETURN
(
 SELECT w.contract_id,
  w.event_id,
  w.event_date,
  w.amount * dbo.GetXR(w.currency_id,@pDisplayIn,w.event_date) AS amount,
  w.product_type
FROM SavingWithdrawals(@pFrom,@pTo) w
WHERE w.currency_id = @pWithdrawedIn OR 0 = @pWithdrawedIn
)

CREATE FUNCTION SavingTransfers_MC
(
@pFrom DATETIME,
@pTo DATETIME,
@pWithdrawedIn INT,
@pDisplayIn INT
) RETURNS TABLE
AS RETURN
(
 SELECT t.from_contract_id,
  t.to_contract_id,
  t.event_id,
  t.event_date,
  t.amount * dbo.GetXR(t.currency_id,@pDisplayIn,t.event_date) AS amount,
  t.product_type
FROM SavingTransfers(@pFrom,@pTo) t
WHERE t.currency_id = @pWithdrawedIn OR 0 = @pWithdrawedIn
)

CREATE FUNCTION [dbo].[SavingCommissions_MC]
(
	@pFrom DATETIME,
	@pTo DATETIME,
	@pWithdrawedIn INT,
	@pDisplayIn INT
) RETURNS TABLE
AS RETURN
(
	SELECT c.contract_id
	, c.event_id
	, c.event_date
	, c.event_code
	, c.amount * dbo.GetXR(c.currency_id,@pDisplayIn,c.event_date) AS amount
	, c.amount_wo_vat * dbo.GetXR(c.currency_id,@pDisplayIn,c.event_date) AS amount_wo_vat
	, c.amount_vat * dbo.GetXR(c.currency_id,@pDisplayIn,c.event_date) AS amount_vat
	, c.product_type
	FROM SavingCommissions(@pFrom,@pTo) c
	WHERE c.currency_id = @pWithdrawedIn OR 0 = @pWithdrawedIn
)

CREATE FUNCTION [dbo].[SavingPenalties_MC]
(
	@pFrom DATETIME,
	@pTo DATETIME,
	@pWithdrawedIn INT,
	@pDisplayIn INT
) RETURNS TABLE
AS RETURN
(
	SELECT p.contract_id
	, p.event_id
	, p.event_date
	, p.event_code
	, p.amount * dbo.GetXR(p.currency_id,@pDisplayIn,p.event_date) AS amount	
	, p.amount_wo_vat * dbo.GetXR(p.currency_id,@pDisplayIn,p.event_date) AS amount_wo_vat
	, p.amount_vat * dbo.GetXR(p.currency_id,@pDisplayIn,p.event_date) AS amount_vat
	, p.product_type
	FROM SavingPenalties(@pFrom,@pTo) p
	WHERE p.currency_id = @pWithdrawedIn OR 0 = @pWithdrawedIn
)
